import React from 'react';
import { hexbin } from 'd3-hexbin'; // Import hexbin separately
import { geoNaturalEarth1 } from 'd3'; // For geographic projection
import { scaleLinear, max } from 'd3'; // Import necessary scale functions
import { Marks } from './Marks';
import { Markscity } from './Markscity';
import ColorLegend from './ColorLegend'; // Import the color legend component

export const BubbleMap = ({ data, worldAtlas }) => {
    const svgWidth = 900;
    const svgHeight = 750;

    const projection = geoNaturalEarth1()
        .center([10.537, 52.210])
        .scale(160000)
        .translate([svgWidth / 2, svgHeight / 2]);

    const hexbinGenerator = hexbin()
        .extent([[0, 0], [svgWidth, svgHeight]])
        .radius(15); // Adjust the radius based on desired hex size

    // Project data with HRV
    const projectedData = data.map(d => {
        const coords = projection(d.coords); // Make sure d.coords is in [longitude, latitude] format
        return { ...d, x: coords[0], y: coords[1], HRV: d.HRV }; // Include HRV here
    });

    // Generate hex bins using projected coordinates
    const bins = hexbinGenerator(projectedData.map(d => [d.x, d.y]));

    // Prepare a color scale for HRV values
    const hrvValues = data.map(d => d.HRV).filter(h => h != null);
    const colorScale = d3.scaleLinear()
        .domain([d3.min(hrvValues), d3.mean(hrvValues), d3.max(hrvValues)]) // Set domain
        .range(['blue', 'lightblue', 'darkblue']); // Define color range


    return (
        <>
            <svg width={svgWidth} height={svgHeight}>
                <Marks bins={bins} data={projectedData} /> {/* Pass bins and the original data */}
                <Markscity data={projectedData} projection={projection} /> {/* Add Markscity layer */}
                <ColorLegend colorScale={colorScale} /> {/* Include color legend */}
            </svg>
        </>
    );
};
