import React from 'react';
import * as d3 from 'd3';

const ColorLegend = ({ colorScale }) => {
    const legendWidth = 300; // Width for the legend
    const legendHeight = 20; // Height for the gradient
    const numTicks = 3; // Number of ticks

    // Generate tick values from the color scale's domain
    const domain = colorScale.domain(); // Get the domain from the color scale
    const ticks = d3.range(numTicks).map(i => 
        d3.scaleLinear()
            .domain([0, numTicks - 1]) // Mapping tick counts
            .range([domain[0], domain[1]])(i) // Map tick positions to HRV values
    );

    return (
        <svg id="color-legend" width={legendWidth} height={legendHeight + 40}>
            <defs>
                <linearGradient id="gradient" x1="100%" y1="0%" x2="0%" y2="0%">
                    {ticks.map((tick, index) => (
                        <stop key={index} 
                              offset={`${(index / (numTicks - 1)) * 100}%`} 
                              stopColor={colorScale(tick)} />
                    ))}
                </linearGradient>
            </defs>
            <rect 
                x="0" 
                y="0" 
                width={legendWidth} 
                height={legendHeight} 
                fill={`url(#gradient)`} 
            />

            {/* Add tick labels */}
            {ticks.map((tick, index) => (
                <text 
                    key={index} 
                    x={(index / (numTicks-1)) * legendWidth} 
                    y={legendHeight + 15} 
                    fontSize="12" 
                    textAnchor="middle">
                    {tick.toFixed()} {/* Format HRV values */}
                </text>
            ))}
            <text 
                x={legendWidth / 2} 
                y={legendHeight + 40} 
                textAnchor="middle" 
                fontWeight="bold">
                Average HRV
            </text>
        </svg>
    );
};

export default ColorLegend;
