import React from 'react';
import * as d3 from 'd3';

const ColorLegend = ({ colorScale }) => {
    const legendWidth = 300; // Example width
    const legendHeight = 20; // Fixed height for the legend

    // Create a linear gradient for the legend
    const gradientId = 'legend-gradient';

    return (
        <svg width={legendWidth} height={legendHeight}>
            <defs>
                <linearGradient id={gradientId} x1="0%" y1="0%" x2="100%" y2="0%">
                    {colorScale.ticks().map(tick => (
                        <stop key={tick} offset={`${(tick - colorScale.domain()[0]) / (colorScale.domain()[1] - colorScale.domain()[0]) * 100}%`} stopColor={colorScale(tick)} />
                    ))}
                </linearGradient>
            </defs>
            <rect x="0" y="0" width={legendWidth} height={legendHeight} fill={`url(#${gradientId})`} />
            {/* Adding labels */}
            {colorScale.ticks().map(tick => (
                <text key={tick} x={((tick - colorScale.domain()[0]) / (colorScale.domain()[1] - colorScale.domain()[0])) * legendWidth} y={legendHeight + 15} fontSize="12" textAnchor="middle">
                    {tick}
                </text>
            ))}
            <text x={legendWidth / 2} y={legendHeight + 30} textAnchor="middle" fontWeight="bold">Average HRV</text>
        </svg>
    );
};

export default ColorLegend;
