import React, { useEffect, useRef } from 'react';
import { geoNaturalEarth1, geoPath, geoGraticule, select, zoom } from 'd3';

const projection = geoNaturalEarth1()
  .scale(5000) // Adjust to zoom level
  .translate([-400, 250]); // Center on Germany

const path = geoPath(projection);
const graticule = geoGraticule();

export const Marks = ({
  worldAtlas: { land, interiors },
  data,
  sizeScale,
  sizeValue,
}) => {
  const svgRef = useRef(); // Reference for the SVG element

  useEffect(() => {
    const svg = select(svgRef.current); // Select SVG element

    // Define zoom behavior
    const zoomBehavior = zoom()
      .scaleExtent([1, 8]) // Set min and max zoom levels
      .on('zoom', (event) => {
        svg.selectAll('.marks').attr('transform', event.transform); // Apply zoom transformation
      });

    svg.call(zoomBehavior); // Bind the zoom behavior to the SVG
  }, []);

  return (
    <g ref={svgRef} className="marks"> {/* Use the ref here for the SVG */}
      <path className="sphere" d={path({ type: 'Sphere' })} />
      <path className="graticules" d={path(graticule())} />
      {land.features.map(feature => (
        <path
          className="land"
          key={feature.id} // Ensure uniqueness for keys
          d={path(feature)}
          style={{ fill: '#ececec' }} // Light fill color for land
        />
      ))}
      <path className="interiors" d={path(interiors)} style={{ fill: 'none', stroke: '#d9dfe0' }} />
      {data.map(d => {
        const [x, y] = projection(d.coords); // Get projected coordinates
        const radius = sizeScale(sizeValue(d)); // Calculate circle radius
        return (
          <circle
            key={`${d['Reported Date']}-${d.coords.join(',')}`}
            cx={x}
            cy={y}
            r={radius}
            style={{ fill: '#4a90e2', opacity: 0.7 }} // Circle color
          />
        );
      })}
    </g>
  );
};
