import React, { useRef, useEffect } from 'react';
import { axisBottom } from 'd3';
import * as d3 from 'd3'; // Import d3 for additional functionalities if needed

const AxisBottom = ({ xScale, innerHeight }) => {
    const ref = useRef();

    useEffect(() => {
        const axis = axisBottom(xScale);
        d3.select(ref.current).call(axis); // Call the d3 axis
    }, [xScale]);

    return <g ref={ref} transform={`translate(0, ${innerHeight})`} />;
};

export default AxisBottom; // Ensure proper default export
