import React from 'react';
import { hexbin } from 'd3-hexbin'; // Import hexbin functionality from d3-hexbin
import { geoNaturalEarth1 } from 'd3'; // Import geographic projection
import { scaleLinear, max } from 'd3'; // Import necessary D3 functions
import { Marks } from './Marks';
import { Markscity } from './Markscity';
import ColorLegend from './ColorLegend'; // Import the color legend


import * as d3 from 'd3'; // Import d3 for scales


export const BubbleMap = ({ data, worldAtlas }) => {
    const svgWidth = 900;
    const svgHeight = 750;

    const projection = geoNaturalEarth1()
        .center([10.537, 52.210])
        .scale(160000)
        .translate([svgWidth / 2, svgHeight / 2]);

    const hexbinGenerator = hexbin()
        .extent([[0, 0], [svgWidth, svgHeight]])
        .radius(7); // Adjust the radius based on desired hex size

    // Project data with HRV
    const projectedData = data.map(d => {
        const coords = projection(d.coords); // d.coords should be [longitude, latitude]
        return { ...d, x: coords[0], y: coords[1], HRV: d.HRV }; // Include HRV here
    });

    // Generate hex bins using projected coordinates
    const bins = hexbinGenerator(projectedData.map(d => [d.x, d.y]));

    return (
        <>
            <svg width={svgWidth} height={svgHeight}>
                <Marks bins={bins} data={projectedData} /> {/* Pass bins and the original data */}
                <Markscity data={projectedData} projection={projection} /> {/* Add Markscity layer */}
            </svg>
        </>
    );
};
