import React from 'react';
import * as d3 from 'd3';

const ColorLegend = ({ colorScale }) => {
    const legendWidth = 300; // Set the legend width
    const legendHeight = 20;  // Fixed height for the legend
    const numTicks = 5;       // Number of ticks along the legend

    // Generate tick values based on the color scale's domain
    const ticks = d3.range(numTicks).map(i => 
        d3.scaleQuantize()
            .domain(colorScale.domain())
            .range(colorScale.ticks(numTicks))[i]
    );

    return (
        <svg width={legendWidth} height={legendHeight + 40}>
            <defs>
                <linearGradient id="gradient" x1="0%" y1="0%" x2="100%" y2="0%">
                    {ticks.map((tick, index) => (
                        <stop 
                            key={tick} 
                            offset={`${(index / (numTicks - 1)) * 100}%`} 
                            stopColor={colorScale(tick)} 
                        />
                    ))}
                </linearGradient>
            </defs>
            <rect x="0" y="0" width={legendWidth} height={legendHeight} fill={`url(#gradient)`} />

            {/* Add tick labels */}
            {ticks.map((tick, index) => (
                <text 
                    key={tick} 
                    x={(index / (numTicks - 1)) * legendWidth} 
                    y={legendHeight + 15} // Placement of label
                    fontSize="12" 
                    textAnchor="middle">
                    {tick.toFixed(2)}  {/* Format the HRV values, adjusting as needed */}
                </text>
            ))}
            <text 
                x={legendWidth / 2} 
                y={legendHeight + 30} 
                textAnchor="middle" 
                fontWeight="bold">
                Average HRV
            </text>
        </svg>
    );
};

export default ColorLegend;
