import React, { useState } from 'react';
import Welcome from './Welcome'; 
import QGIS from './QGIS'; 
import Declaration from './Declaration'; // Import the Declaration component
import { useWorldAtlas } from './useWorldAtlas';
import './App.css';
import { useData } from './useData';
import { Hexbinmap } from './HexbinMap/index.js';
import { DateHistogram } from './DateHistogram/index.js';
import { scaleLinear } from 'd3';
import ColorLegend from './HexbinMap/ColorLegend.js'; 
import * as d3 from 'd3';
import Filters from './Filters'; 
import Instructions from './Instructions'; 

const width = 900;
const height = 1000;
const dateHistogramSize = 0.24;
const orderedDays = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"]; 

const App = () => {
    const [currentPage, setCurrentPage] = useState('declaration'); // Start with Declaration
    const worldAtlas = useWorldAtlas();
    const data = useData(); 
    const [brushExtent, setBrushExtent] = useState(null);
    const [participant, setParticipant] = useState("All");
    const [gender, setGender] = useState("Both");
    const [modeButton, setModeButton] = useState("Both");
    const [yValueField, setYValueField] = useState("HRV");
    const [hexbinSize, setHexbinSize] = useState(5);
    const [errorMessage, setErrorMessage] = useState(''); // Track error message

    const handleStartDeclaration = () => setCurrentPage('welcome'); // Go to Welcome screen
    const handleGoToQGIS = () => setCurrentPage('qgis'); // Go to QGIS page
    const handleBackToD3React = () => setCurrentPage('mainapp'); // Go back to D3 React App

    const handleFinish = () => {
        if (!isChecked) {
            setErrorMessage('You must confirm that you have finished the D3-React questionnaire before finish this experiment.'); // Set error message if not checked
        } else {
            setErrorMessage(''); // Clear error message if checked
            setCurrentPage('final'); // Go to final page
        }
    };

    const renderPage = () => {
        switch (currentPage) {
            case 'declaration':
                return <Declaration onStart={handleStartDeclaration} />;
            case 'welcome':
                return <Welcome onQGIS={handleGoToQGIS} />; // Pass handler to Welcome
            case 'qgis':
                return <QGIS onBack={handleBackToD3React} />; // QGIS page
            case 'mainapp':
                return renderMainApp(); // Render D3 React App
                case 'final':
                    return (
                        <div className="final-message">
                            Thank you for completing the experiment process!
                            <br />
                            <br />
                            Best Regards,
                            <div>Febryeric M. Parantean</div>
                        </div>
                    );
            default:
                return <Declaration onStart={handleStartDeclaration} />;
        }
    };

    const renderMainApp = () => {
        if (!worldAtlas || !data || data.length === 0) {
            return (
                <svg width={width} height={height}>
                    <text x={width / 2} y={height / 2} textAnchor="middle" fontSize="14" fill="black">
                        Loading...
                    </text>
                </svg>
            ); 
        }

        // Get unique participants dynamically and sort them
        const uniqueParticipants = Array.from(new Set(data.map(d => d.Participant)))
            .sort((a, b) => a - b); 

        // Filter Data based on selected criteria
        const filteredData = data.filter(d => {
            const participantFilter = participant === "All" || d.Participant.toString() === participant;
            const genderFilter = gender === "Both" || d.Gender === gender; 
            const modeButtonFilter = modeButton === "Both" || d.ModeButton_xs === modeButton;
            return participantFilter && genderFilter && modeButtonFilter;
        });

        const hrvValues = filteredData.map(d => d[yValueField]).filter(h => h != null); 
        const colorScale = scaleLinear()
            .domain([d3.min(hrvValues) || 0, d3.mean(hrvValues) || 0, d3.max(hrvValues) || 0])
            .range(['yellow', 'orange', 'red']); 

        return (
            <div style={{ display: 'flex' }}>
                <div className="filters">
                    <Filters 
                        participant={participant} 
                        setParticipant={setParticipant} 
                        gender={gender} 
                        setGender={setGender} 
                        modeButton={modeButton} 
                        setModeButton={setModeButton}
                        yValueField={yValueField}
                        setYValueField={setYValueField}
                        uniqueParticipants={uniqueParticipants} 
                        hexbinSize={hexbinSize}
                        setHexbinSize={setHexbinSize}
                    />
                    <img 
                        src="bus.png" 
                        alt="bus Logo" 
                        style={{ width: '100px', height: 'auto', marginTop: '210px', marginLeft: '35px', alignSelf: 'center' }} 
                    />
                </div>

                <svg width={width} height={height}>
                    <text x={width / 2} y={45} textAnchor="middle" className="title">
                        Travel Experience with Different Transportation Mode
                    </text>
                    <image 
                        id='map-image'
                        href="datamap.png" 
                        x={21}
                        y={72} 
                        height={height} 
                        width={width} 
                    />
                    <g transform={`translate(0,30)`}>
                        <Hexbinmap 
                            data={brushExtent ? filteredData.filter(d => brushExtent.includes(orderedDays[d.DayOrder])) : []}
                            worldAtlas={worldAtlas} 
                            yValueField={yValueField} 
                            hexbinSize={hexbinSize} 
                        />
                    </g>
                    <g transform={`translate(0, 700)`}>
                        <DateHistogram
                            data={filteredData} 
                            width={width}
                            height={dateHistogramSize * height}
                            setBrushExtent={setBrushExtent}
                            xValue={d => d.DayOrder} 
                            yValueField={yValueField} 
                        />
                    </g>
                    <g transform={`translate(820, 550)`}>
                        <ColorLegend 
                            colorScale={colorScale} 
                            yValueField={yValueField} 
                            data={filteredData}  
                        /> 
                    </g>
                </svg>

                <Instructions />

                {/* Confirmation Checkbox and Buttons */}
                <div style={{ margin: '90px', marginTop:'750px' }}>
                    <input
                        type="checkbox"
                        checked={isChecked}
                        onChange={() => {
                            setIsChecked(!isChecked);
                            setErrorMessage(''); // Clear error message on change
                        }}
                    />
                    <label style={{ marginLeft: '5px' }}>
                    I have finished and submitted the D3-React questionnaire in the Google Form
                    </label>
                    {/* Display Error Message */}
                    {errorMessage && (
                        <div style={{ color: 'red', marginTop: '10px' }}>
                            {errorMessage}
                        </div>
                    )}

                    <div style={{ marginTop: '10px' }}>
                        <a href="https://forms.gle/8BJUm4goDgqYuvCw7" target="_blank" rel="noopener noreferrer">
                            <button className="d3-que" style={{ marginLeft: '70px', padding: '10px 20px' }}>
                                Go to D3-React Questionnaire
                            </button>
                        </a>
                        <button
                            onClick={handleFinish}
                            className="backd3"
                            style={{ marginLeft: '30px', padding: '10px 20px' }} // Same styling as above

                        >
                            Finish
                        </button>
                    </div>


                </div>
            </div>
        );
    };

    return <div>{renderPage()}</div>;
};

export default App;
