import React from 'react';
import { scaleSqrt, max } from 'd3';
import { Marks } from './Marks';
import { Markscity } from './Markscity';
import { hexbin } from 'd3-hexbin';
import { geoNaturalEarth1 } from 'd3';

const sizeValue = d => d.HRV;

export const BubbleMap = ({ data, worldAtlas }) => {
    const projection = geoNaturalEarth1()
        .center([10.537004, 52.210297])
        .scale(160000)
        .translate([450, 300]);

    const maxRadius = 8;

    const sizeScale = scaleSqrt()
        .domain([0, max(data, sizeValue)])
        .range([0, maxRadius]);

    const hexbinGenerator = hexbin()
        .size([900, 750]) // Adjust to your SVG size
        .radius(20);

    const bins = hexbinGenerator(data.map(d => projection(d.coords)));

    return (
        <>
            <Marks
                worldAtlas={worldAtlas}
                bins={bins}  
                sizeValue={sizeValue}
                projection={projection} 
            />
            <Markscity
                worldAtlas={worldAtlas}
            />
        </>
    );
};
