import React, { useEffect } from 'react';
import * as d3 from 'd3';

const ColorLegend = ({ colorScale }) => {
    useEffect(() => {
        const svg = d3.select('#color-legend')
            .attr("width", 300)
            .attr("height", 50);

        const legendWidth = 260;
        const legendHeight = 20;
        svg.selectAll("*").remove();

        // Define the gradient
        const gradient = svg.append("defs")
            .append("linearGradient")
            .attr("id", "gradient")
            .attr("x1", "0%")
            .attr("y1", "0%")
            .attr("x2", "100%")
            .attr("y2", "0%");

        // Create gradient stops
        const ticks = colorScale.ticks(5);
        ticks.forEach((tick, index) => {
            gradient.append("stop")
                .attr("offset", `${(index / (ticks.length - 1)) * 100}%`)
                .attr("stop-color", colorScale(tick));
        });

        svg.append("rect")
            .attr("x", 0)
            .attr("y", 0)
            .attr("width", legendWidth)
            .attr("height", legendHeight)
            .style("fill", "url(#gradient)");

        // Tick labels
        ticks.forEach((tick, index) => {
            svg.append("text")
                .attr("x", (index / (ticks.length - 1)) * legendWidth)
                .attr("y", legendHeight + 15)
                .attr("text-anchor", "middle")
                .text(tick.toFixed(2));
        });

        // Title
        svg.append("text")
            .attr("x", legendWidth / 2)
            .attr("y", legendHeight -30)
            .attr("text-anchor", "middle")
            .text("Average HRV")
            .attr("font-weight", "bold");

    }, [colorScale]);

    // Set positioning in CSS rather than JS
    return <svg id="color-legend" style={{ position: 'absolute', top: '20px', left: '700px' }}></svg>; // Example of setting position
};

export default ColorLegend;
