import React, { useState } from 'react';

const Welcome = ({ onQGIS }) => {
    const [isChecked, setIsChecked] = useState(false); // State to track checkbox
    const [errorMessage, setErrorMessage] = useState(''); // State for error message

    const handleCheckboxChange = () => {
        setIsChecked(!isChecked); // Toggle checkbox state
        setErrorMessage(''); // Clear error message when checkbox changes
    };

    const handleClick = () => {
        if (!isChecked) {
            setErrorMessage('You must confirm that you understand before proceeding.'); // Set error message
        } else {
            onQGIS(); // Proceed to QGIS if checkbox is checked
        }
    };

    return (
        <div className="welcome-container">
            <h1 className="welcome-title">Instruction</h1>
            <p className="welcome-description">
                The data visualization is about travel experience with different transportation modes. 
                The static map and interactive map data visualization were created and developed by Quantum Geographic Information System (QGIS) software and D3.js with React respectively.
            </p>
            <p className="welcome-description">
                First, you will explore QGIS map and then D3-React map. You need to gain the information as much as you can. There is no limited time on this experiment, and you can make your own notes.
                Once you finish with the map click the questionnaire button and fill in the form.  After completing the two questionnaires, then you can click the "Finish" button.
            </p>
            <p className="welcome-description">

            </p>
            <p className="welcome-description">
                Here is the step of this experiment:
                <ul>
                    <li>Explore QGIS map</li>
                    <li>Fill in the first questionnaire</li>
                    <li>Explore D3.js React Map</li>
                    <li>Fill in the second questionnaire</li>
                </ul>
            </p>
            <div>
                <input 
                    type="checkbox" 
                    checked={isChecked} 
                    onChange={handleCheckboxChange} 
                />
                <label>I declare that I have read and understand the instructions</label>
            </div>
            {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}
            <button onClick={handleClick} className="welcome-button">
                Go to QGIS Map           
            </button>
        </div>
    );
};

export default Welcome;
