import React from 'react';
import { scaleSqrt, max } from 'd3';
import { Marks } from './Marks';
import { Markscity } from './Markscity';


const sizeValue = d => d.HRV;

export const BubbleMap = ({ data, worldAtlas }) => {
  const maxRadius = 8;

  const sizeScale = scaleSqrt()
    .domain([0, max(data, sizeValue)])
    .range([0, maxRadius]);

    return (
      <svg width={960} height={700}> {/* Adjust width and height as needed */}
      {/* Title for the HRV Distribution Map */}
      <text
        x={480} // Center horizontally (half of width)
        y={40} // Position it near the top
        textAnchor="middle" // Center the text horizontally
        fontSize="24" // Set font size
        fontWeight="bold" // Make the title bold
        fill="black" // Color of the title
      >
        THE HRV DISTRIBUTION
      </text>

      <Marks
        worldAtlas={worldAtlas}
        data={data}
        sizeScale={sizeScale}
        sizeValue={sizeValue}
      />
      <Markscity
        worldAtlas={worldAtlas}
      />
    </svg>
  );
};
