import React, { useState } from 'react';
import { hexbin as d3Hexbin } from 'd3-hexbin';
import * as d3 from 'd3';

export const Marks = ({ bins }) => {
    const [tooltip, setTooltip] = useState({ display: 'none' });

    // Define the color scale
    const hrvValues = data.map(d => d.HRV);
    const colorScale = d3.scaleLinear()
        .domain([d3.min(hrvValues), d3.mean(hrvValues), d3.max(hrvValues)]) // Set your thresholds
        .range(['white', 'lightblue', 'blue', 'darkblue']); // Set your color range

    return (
        <g className="marks">
            {bins.map((bin, i) => {
                // Use the color scale to get the fill color based on bin length
                const fillColor = colorScale(bin.length);

                return (
                    <g key={i} 
                       onMouseEnter={(e) => {
                           setTooltip({
                               display: 'block',
                               x: e.clientX,
                               y: e.clientY,
                               content: `Count: ${bin.length}`,
                           });
                       }}
                       onMouseLeave={() => {
                           setTooltip({ display: 'none' });
                       }}>
                    <path
                        d={d3Hexbin().hexagon(5)} // Adjust the hexagon size
                        transform={`translate(${bin.x}, ${bin.y})`} // Position using centroid
                        fill={fillColor} // Apply the dynamic color
                        stroke="black" // Stroke color
                        strokeWidth={1} // Stroke width
                        opacity={0.7} // Adjust opacity if desired
                    />
                    </g>
                );
            })}
            {/* Tooltip */}
            <text 
                x={tooltip.x} 
                y={tooltip.y} 
                style={{ display: tooltip.display, pointerEvents: 'none' }} 
                fill="black" 
                fontSize="12" 
                textAnchor="middle">
                {tooltip.content}
            </text>
        </g>
    );
};
