import React from 'react';
import { MapContainer, TileLayer } from 'react-leaflet'; // Importing Leaflet components
import { useData } from './useData'; // Import the hook to get data
import BubbleMap from './BubbleMap/index.js'; // Import the BubbleMap component
import './App.css'; 
import 'leaflet/dist/leaflet.css'; // Leaflet styles

const App = () => {
    const data = useData(); // Load data using the custom hook

    if (!data || data.length === 0) {
        return <div>Loading...</div>; // Display loading message
    }

    return (
        <div style={{ height: "100vh" }}>
            <MapContainer center={[52.3, 10.5]} zoom={10} style={{ height: "100%", width: "100%" }}>
                <TileLayer
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    attribution="&copy; OpenStreetMap contributors"
                />
                <BubbleMap data={data} /> {/* Render BubbleMap with loaded data */}
            </MapContainer>
        </div>
    );
};

export default App;
