import React, { useEffect } from 'react'; 
import { useMap } from 'react-leaflet'; // Access the Leaflet map context
import L from 'leaflet'; // Import Leaflet
import { hexbin } from 'd3-hexbin'; // Import D3 hexbin for creating hexagons
import * as d3 from 'd3'; // Import D3 utilities

export const BubbleMap = ({ data }) => {
    const map = useMap(); // Access the Leaflet map instance

    useEffect(() => {
        // Create hexbin layer and add it to the map
        const hexbinLayer = L.hexbinLayer({
            radius: 20, // Set hexagon radius
            opacity: 0.6, // Hexagon opacity
            colorRange: ['#f7fbff', '#08306b'], // Color range for the hexagons
        }).addTo(map);

        // Prepare data for hexbin layer
        const hexPoints = data.map(feat => feat.coords); // Expect coords as [lng, lat]

        // Set data to hexbin layer
        if (hexPoints.length) {
            hexbinLayer.data(hexPoints);
        }

        // Cleanup: Remove hexbin layer when component unmounts
        return () => {
            if (map.hasLayer(hexbinLayer)) {
                map.removeLayer(hexbinLayer);
            }
        };
    }, [data, map]); // Effect dependencies

    return null; // No direct rendering
};

export default BubbleMap;
