import React, { useState } from 'react';
import * as d3 from 'd3'; // Import d3
import { hexbin } from 'd3-hexbin'; // Import hexbin specifically

export const Marks = ({ bins, data }) => {
    const [tooltip, setTooltip] = useState({ display: 'none' });

    // Prepare the color scale for HRV values
    const hrvValues = data.map(d => d.HRV); // Collect all HRV values
    const colorScale = d3.scaleLinear()
        .domain([d3.min(hrvValues), d3.mean(hrvValues), d3.max(hrvValues)]) // Define domain for colors based on HRV
        .range(['blue', 'lightblue', 'darkblue']); // Color range based on HRV values

    return (
        <g className="marks">
            {bins.map((bin, i) => {
                // Filter data points that belong to this bin
                const binData = data.filter(d => 
                    d.x >= bin.x - 15 && d.x <= bin.x + 15 && // Check if point is within the x range
                    d.y >= bin.y - 15 && d.y <= bin.y + 15    // Check if point is within the y range
                );

                // Calculate average HRV for this bin
                const averageHRV = binData.length > 0 
                    ? binData.reduce((sum, d) => sum + d.HRV, 0) / binData.length 
                    : 0;

                const fillColor = colorScale(averageHRV); // Set the color based on calculated average HRV

                return (
                    <g key={i} 
                       onMouseEnter={(e) => {
                           setTooltip({
                               display: 'block',
                               x: e.clientX,
                               y: e.clientY,
                               content: `Count: ${bin.length}, Avg HRV: ${averageHRV.toFixed(2)}`,
                           });
                       }}
                       onMouseLeave={() => {
                           setTooltip({ display: 'none' });
                       }}>
                    <path
                        d={d3.hexbin().hexagon(15)} // Create hexagon shape
                        transform={`translate(${bin.x}, ${bin.y})`} // Position hexagon
                        fill={fillColor} // Apply the dynamic color
                        stroke="black" // Stroke color
                        strokeWidth={1} // Stroke width
                        opacity={0.7} // Adjust opacity if needed
                    />
                    </g>
                );
            })}
            {/* Tooltip */}
            <text 
                x={tooltip.x} 
                y={tooltip.y} 
                style={{ display: tooltip.display, pointerEvents: 'none' }} 
                fill="black" 
                fontSize="12" 
                textAnchor="middle">
                {tooltip.content}
            </text>
        </g>
    );
};
