import { useState, useEffect } from 'react';
import { json } from 'd3';
import { feature, mesh } from 'topojson-client'; // Ensure d3-topology is installed

const jsonUrl = 'https://unpkg.com/world-atlas@2.0.2/countries-50m.json';

export const useWorldAtlas = () => {
  const [data, setData] = useState(null);
  const [error, setError] = useState(null); // Add error state
  const [loading, setLoading] = useState(true); // Add loading state

  useEffect(() => {
    json(jsonUrl)
      .then(topology => {
        const { countries, land } = topology.objects;
        setData({
          land: feature(topology, land),
          interiors: mesh(topology, countries, (a, b) => a !== b)
        });
        setLoading(false); // Set loading to false on success
      })
      .catch(err => {
        console.error("Error loading world atlas data:", err);
        setError(err); // Set error to enable error handling in components
        setLoading(false);
      });
  }, []);

  return { data, loading, error }; // Return loading and error states
};
