import { geoNaturalEarth1, geoPath, geoGraticule } from 'd3';

const projection = geoNaturalEarth1()
  .center([10.527004, 52.210297]) // Center over Braunschweig, Germany
  .scale(150000) // Adjust scale for zoom level; increase for more detail
  .translate([450, 300]); // Adjust based on your SVG dimensions

const locations = [
  { name: "Braunschweig Rathaus", coords: [10.5263, 52.2643] },
  { name: "Braunschweig Hbf", coords: [10.5396, 52.2520] },
  { name: "Wolfenb端ttel Banhoff", coords: [10.5320, 52.1592] },
  { name: "Wolfenb端ttel Schloss", coords: [10.5316, 52.1627] },
];

export const Markscity = () => (
  <g className="markscity">
    {locations.map((location, index) => {
      const [x, y] = projection(location.coords); // Project the coordinates
      return (
        <g key={index}> {/* Use index for unique keys */}
          <circle 
            cx={x} 
            cy={y} 
            r={5}
            fill="black" // Set fill color to black
          />
          <text
            className="braunschweig"
            textAnchor="left" // Center the text
            x={x} 
            y={y - 10} // Position text above the circle
          >
            {location.name}
          </text>
        </g>
      );
    })}
  </g>
);
