import React, { useState } from 'react';
import { hexbin as d3Hexbin } from 'd3-hexbin';
import * as d3 from 'd3';

export const Marks = ({ bins }) => {
    const [tooltip, setTooltip] = useState({ display: 'none' });

    // Calculate the maximum length for the color scale domain
    const maxLength = d3.max(bins, d => d.length) || 1;

    // Define the color scale
    const colorScale = d3.scaleLinear()
        .domain([0, maxLength]) // From 0 to max bin length
        .range(["black", "darkblue"]); // Color range based on bin count

    return (
        <g className="marks">
            {bins.map((bin, i) => (
                <g key={i} 
                   onMouseEnter={(e) => {
                       setTooltip({
                           display: 'block',
                           x: e.clientX,
                           y: e.clientY,
                           content: `Count: ${bin.length}`,
                       });
                   }}
                   onMouseLeave={() => {
                       setTooltip({ display: 'none' });
                   }}>
                    <path
                        d={d3Hexbin().hexagon(10)} // Create hexagon shape
                        transform={`translate(${bin.x}, ${bin.y})`} // Position using centroid
                        fill={colorScale(bin.length)} // Apply color from the scale
                        stroke="red" // Set stroke color
                        strokeWidth={1}
                        opacity={0.5} // Stroke width
                    />
                </g>
            ))}
            {/* Tooltip */}
            <text 
                x={tooltip.x} 
                y={tooltip.y} 
                style={{ display: tooltip.display, pointerEvents: 'none' }} 
                fill="black" 
                fontSize="12" 
                textAnchor="middle">
                {tooltip.content}
            </text>
        </g>
    );
};
