// DateHistogram.js
import {
    scaleLinear,
    scaleTime,
    max,
    timeFormat,
    extent,
    brushX,
    select
  } from 'd3';
  import { useRef, useEffect } from 'react';
  import { AxisBottom } from './AxisBottom';
  import { AxisLeft } from './AxisLeft';
  import { Marks } from './Marks';

  const margin = { top: 20, right: 30, bottom: 50, left: 90 };

  export const DateHistogram = ({
    data,
    width,
    height,
    setBrushExtent,
    xValue
  }) => {
    const innerHeight = height - margin.top - margin.bottom;
    const innerWidth = width - margin.left - margin.right;

    const xScale = scaleTime()
      .domain(extent(data, xValue))
      .range([0, innerWidth])
      .nice();

    const yValue = d => d['Total Dead and Missing'];
    const yScale = scaleLinear()
      .domain([0, max(data, yValue)])
      .range([innerHeight, 0])
      .nice();

    const brushRef = useRef();

    useEffect(() => {
      const brush = brushX().extent([[0, 0], [innerWidth, innerHeight]]);
      brush(select(brushRef.current));
      brush.on('brush end', (event) => {
        setBrushExtent(event.selection && event.selection.map(xScale.invert));
      });
    }, [innerWidth, innerHeight]);

    return (
      <>
        <rect width={width} height={height} fill="white" />
        <g transform={`translate(${margin.left},${margin.top})`}>
          <AxisBottom
            xScale={xScale}
            innerHeight={innerHeight}
            tickFormat={timeFormat('%m/%d/%Y')}
          />
          <AxisLeft
            yScale={yScale}
            innerWidth={innerWidth}
          />
          <Marks
            data={data}
            xScale={xScale}
            yScale={yScale}
            xValue={xValue}
            yValue={yValue}
            circleRadius={3}
          />
          <g ref={brushRef} />

          {/* Add X Axis Label */}
          <text
            className="axis-label"
            textAnchor="middle"
            transform={`translate(${innerWidth / 2}, ${innerHeight + 30})`} // Adjust position
          >
            Time
          </text>

          {/* Add Y Axis Label */}
          <text
            className="axis-label"
            textAnchor="middle"
            transform={`translate(-20, ${innerHeight / 2}) rotate(-90)`} // Adjust position
          >
            Total Dead and Missing
          </text>
        </g>
      </>
    );
  };
