import {
    scaleLinear,
    scaleBand,
    max,
    brushX,
    select,
    min
} from 'd3';
import { useRef, useEffect } from 'react';
import { AxisBottom } from './AxisBottom';
import { AxisLeft } from './AxisLeft';
import { Marks } from './Marks';

const margin = { top: 20, right: 10, bottom: 50, left: 90 };

export const DateHistogram = ({
    data,
    width,
    height,
    setBrushExtent,
    yValueField
}) => {
    const innerHeight = height - margin.top - margin.bottom;
    const innerWidth = width - margin.left - margin.right;

    const xScale = scaleBand()
        .domain(["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"])
        .range([0, innerWidth])
        .padding(0.1);

    // Define a function to get the Y value
    const yValue = d => d[yValueField];

    // Filter out data with invalid Y values (null or equal to 0.00)
    const filteredData = data.filter(d => d[yValueField] != null && d[yValueField] !== 0);

    const yScale = scaleLinear()
        .domain([min(filteredData, yValue), max(filteredData, yValue)]) // Ensure max returns a number
        .range([innerHeight, 0])
        .nice();

    const brushRef = useRef();

    useEffect(() => {
        const brush = brushX().extent([[0, 0], [innerWidth, innerHeight]]);
        brush(select(brushRef.current));

        // Set initial brush extent to cover half of the histogram
        const initialSelection = [0, innerWidth / 2]; // Selecting the first half

        // Set the initial brush selection
        brush.move(select(brushRef.current), initialSelection);

        brush.on('brush end', (event) => {
            const selection = event.selection;
            if (!selection) return;

            const selectedDays = xScale.domain().filter(day => {
                const dayPosition = xScale(day);
                return dayPosition >= selection[0] && dayPosition <= selection[1];
            });

            setBrushExtent(selectedDays); 
        });
    }, [innerWidth, innerHeight, setBrushExtent, xScale]); // Include xScale

    const getYAxisLabel = (field) => {
        switch (field) {
            case 'HR_mad_filtered':
                return 'HR Mad Score';
            case 'HRV':
                return 'HRV Score';
            case 'stress_xs':
                return 'Stress Score';
            case 'satisfaction_journey_xs':
                return 'Satisfaction Score';
            default:
                return 'Score';
        }
    };

    return (
        <>
            <rect width={width} height={height} fill="white" />
            <g transform={`translate(${margin.left},${margin.top})`}>
                <AxisBottom
                    xScale={xScale}
                    innerHeight={innerHeight + 10}
                    tickFormat={d => d}
                />
                <AxisLeft
                    yScale={yScale}
                    innerWidth={innerWidth}
                />
                <Marks
                    data={filteredData} // Use filtered data here
                    xScale={xScale}
                    yScale={yScale}
                    xValue={d => d.DayOrder}  
                    yValue={yValue}
                    circleRadius={2.5}
                />
                <g ref={brushRef} />
                <text
                    className="axis-label"
                    textAnchor="middle"
                    transform={`translate(${innerWidth / 2}, ${innerHeight + 44})`} 
                >
                    Days of the Week
                </text>
                <text
                    className="axis-label"
                    textAnchor="middle"
                    transform={`translate(-30, ${innerHeight / 2}) rotate(-90)`} 
                >
                    {getYAxisLabel(yValueField)}
                </text>
            </g>
        </>
    );
};
