// App.js
import React, { useState } from 'react';
import './App.css';
import { useData } from './useData';
import BubbleMap from './BubbleMap/index.js'; // The BubbleMap should have a default export
import { DateHistogram } from './DateHistogram/index.js'; // Same for DateHistogram

const App = () => {
    const data = useData(); // Your data loading logic
    const [brushExtent, setBrushExtent] = useState(null);

    if (!data || data.length === 0) {
        return <pre>Loading...</pre>; // Provide feedback while loading
    }

    const filteredData = brushExtent 
        ? data.filter(d => {
            const date = d.Timestamp; // Use Timestamp for filtering
            return date >= brushExtent[0] && date <= brushExtent[1];
        })
        : data;

    return (
        <div>
            <BubbleMap data={filteredData} /> {/* Render your map */}
            <DateHistogram
                data={data} // Pass original dataset for the histogram
                width={960}
                height={300}
                setBrushExtent={setBrushExtent}
                xValue={d => d.Timestamp} // X-axis function
            />
        </div>
    );
};

export default App;
