import {
    scaleLinear,
    scaleBand,
    max,
    brushX,
    select
} from 'd3';
import { useRef, useEffect } from 'react';
import { AxisBottom } from './AxisBottom';
import { AxisLeft } from './AxisLeft';
import { Marks } from './Marks';

const margin = { top: 20, right: 60, bottom: 50, left: 90 };

export const DateHistogram = ({
    data,
    width,
    height,
    setBrushExtent,
}) => {
    const innerHeight = height - margin.top - margin.bottom;
    const innerWidth = width - margin.left - margin.right;

    const xScale = scaleBand()
        .domain(["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"])
        .range([0, innerWidth])
        .padding(0.1); // Adjust padding as necessary

    const yValue = d => d.HRV; // Use HRV for y values
    const yScale = scaleLinear()
        .domain([-5, max(data, yValue) || 200]) // Ensure max returns a number
        .range([innerHeight, 0])
        .nice();

    const brushRef = useRef();

    useEffect(() => {
        const brush = brushX().extent([[0, 0], [innerWidth, innerHeight]]);
        brush(select(brushRef.current));

        brush.on('brush end', (event) => {
            const selection = event.selection;
            if (!selection) return;

            // Get the selected domains based on brush selection
            const selectedDays = xScale.domain().filter(day => {
                const dayPosition = xScale(day);
                return dayPosition >= selection[0] && dayPosition <= selection[1];
            });

            // Update the brush extent
            setBrushExtent(selectedDays); 
        });
    }, [innerWidth, innerHeight, setBrushExtent]);

    return (
        <>
            <rect width={width} height={height} fill="white" />
            <g transform={`translate(${margin.left},${margin.top})`}>
                <AxisBottom
                    xScale={xScale}
                    innerHeight={innerHeight + 5}
                    tickFormat={d => d}
                />
                <AxisLeft
                    yScale={yScale}
                    innerWidth={innerWidth}
                />
                <Marks
                    data={data}
                    xScale={xScale}
                    yScale={yScale}
                    xValue={d => d.DayOrder}  // Reference DayOrder
                    yValue={yValue}
                    circleRadius={2.5}
                />
                <g ref={brushRef} />
                <text
                    className="axis-label"
                    textAnchor="middle"
                    transform={`translate(${innerWidth / 2}, ${innerHeight + 35})`} 
                >
                    Days of the Week
                </text>
                <text
                    className="axis-label"
                    textAnchor="middle"
                    transform={`translate(-25, ${innerHeight / 2}) rotate(-90)`} 
                >
                    HRV Score
                </text>
            </g>
        </>
    );
};
