import { geoNaturalEarth1, geoPath, geoGraticule } from 'd3';

const projection = geoNaturalEarth1()
  .center([10.527004, 52.210297]) // Center over Braunschweig, Germany
  .scale(150000) // Adjust scale for zoom level; increase for more detail
  .translate([450, 300]); // Adjust based on your SVG dimensions; // Original projection
const path = geoPath(projection);
const graticule = geoGraticule();

export const Marks = ({
  worldAtlas: { land, interiors },
  data,
  sizeScale,
  sizeValue
}) => (
  <g className="marks">
    <path className="sphere" d={path({ type: 'Sphere' })} />
    <path className="graticules" d={path(graticule())} />
    {land.features.map(feature => (
      <path className="land" d={path(feature)} />
    ))}
    <path className="interiors" d={path(interiors)} />
    {data.map(d => {
      const [x, y] = projection(d.coords);
      const fillColor = sizeValue(d) > 50 ? 'red' : 'black';
      return <circle 
                key={d.id}
                cx={x} 
                cy={y} 
                r={sizeScale(sizeValue(d))}
                stroke="black" 
                />
                ;
    })}
  </g>

);
