import React, { useEffect, useRef } from 'react';
import { geoNaturalEarth1, geoPath, geoGraticule, zoom } from 'd3';

const projection = geoNaturalEarth1()
  .scale(1500) // Adjust the scale as needed
  .translate([width / 2, height / 2]); // Adjust based on your SVG width/height

const path = geoPath(projection);
const graticule = geoGraticule();

export const Marks = ({
  worldAtlas: { land, interiors },
  data,
  sizeScale,
  sizeValue,
  width,
  height,
}) => {
  const svgRef = useRef();

  useEffect(() => {
    const svg = d3.select(svgRef.current);

    const zoomBehavior = zoom()
      .scaleExtent([1, 8]) // Minimum and maximum zoom
      .on('zoom', (event) => {
        svg.selectAll('.marks').attr('transform', event.transform);
      });

    svg.call(zoomBehavior); // Bind the zoom behavior to the SVG
  }, []);

  return (
    <g ref={svgRef} className="marks">
      <path className="sphere" d={path({ type: 'Sphere' })} />
      <path className="graticules" d={path(graticule())} />
      {land.features.map(feature => (
        <path
          className="land"
          key={feature.id}
          d={path(feature)}
          style={{ fill: '#ececec' }}
        />
      ))}
      <path className="interiors" d={path(interiors)} style={{ fill: 'none', stroke: '#d9dfe0' }} />
      {data.map(d => {
        const [x, y] = projection(d.coords);
        const radius = sizeScale(sizeValue(d));
        return (
          <circle
            key={`${d['Reported Date']}-${d.coords.join(',')}`}
            cx={x}
            cy={y}
            r={radius}
            style={{ fill: '#4a90e2', opacity: 0.7 }}
          />
        );
      })}
    </g>
  );
};
