// BubbleMap/index.js
import React from 'react';
import { useMap } from 'react-leaflet'; 
import L from 'leaflet';
import { hexbin } from 'd3-hexbin'; 
import * as d3 from 'd3'; 

const BubbleMap = ({ data }) => {
    const map = useMap(); 

    const colorScale = d3.scaleLinear()
        .domain([d3.min(data, d => d.HRV), d3.mean(data, d => d.HRV), d3.max(data, d => d.HRV)]) 
        .range(['lightblue', 'blue', 'darkblue']); 

    const hexbinGenerator = hexbin()
        .radius(20) 
        .x(d => d[0]) 
        .y(d => d[1]); 

    const points = data.map(feat => feat.coords); 

    const bins = hexbinGenerator(points); 

    const hexbinLayer = L.layerGroup().addTo(map); 

    bins.forEach(bin => {
        const avgHRV = d3.mean(bin.map(d => 
            data.find(feature => 
                feature.coords[0] === d[0] && 
                feature.coords[1] === d[1]).HRV
        ));

        const hexCoords = generateHexagon(bin.x, bin.y, 20); 

        L.polygon(hexCoords, {
            color: colorScale(avgHRV),
            fillColor: colorScale(avgHRV),
            fillOpacity: 0.6,
            weight: 1,
        }).addTo(hexbinLayer); 
    });

    return null; 
};

const generateHexagon = (lat, lng, radius) => {
    const angle = (Math.PI / 3);
    const points = [];
    for (let i = 0; i < 6; i++) {
        points.push([
            lat + (radius * Math.sin(angle * i)),
            lng + (radius * Math.cos(angle * i))
        ]);
    }
    return points; 
};

// **Ensure this is a default export**
export default BubbleMap;
