import { geoNaturalEarth1 } from 'd3';
import { hexbin } from 'd3-hexbin'; // Import hexbin

const projection = geoNaturalEarth1()
    .center([10.537004, 52.210297]) // Center over Braunschweig, Germany
    .scale(160000)
    .translate([450, 300]);

export const Marks = ({ bins, sizeValue }) => (
    <g className="marks">
        {bins.map((bin, i) => {
            const size = sizeValue(bin) || 0; // Calculate size if necessary
            const [x, y] = hexbin(); // Use hexbin to get the center of the hexagon
            return (
                <path
                    key={i}
                    d={hexbin.hexagon(10)} // Hexagon radius
                    transform={`translate(${x}, ${y})`} // Position the hexagon
                    fill="orange" // Adjust color as needed
                    stroke="black"
                    strokeWidth={1}
                />
            );
        })}
    </g>
);
