import React, { useState } from 'react';
import { useWorldAtlas } from './useWorldAtlas';
import './App.css';
import { useData } from './useData';
import { Hexbinmap } from './HexbinMap/index.js';
import { DateHistogram } from './DateHistogram/index.js';
import { scaleLinear } from 'd3';
import ColorLegend from './HexbinMap/ColorLegend.js'; // Import ColorLegend
import * as d3 from 'd3'; // Import d3 for scales

const width = 900;
const height = 750;
const dateHistogramSize = 0.27;
const orderedDays = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"]; // Adjusted order

const App = () => {
    const worldAtlas = useWorldAtlas();
    const data = useData(); // Use the JSON data

    const [brushExtent, setBrushExtent] = useState(null);

    if (!worldAtlas || !data || data.length === 0) {
        return (
          <svg width={width} height={height}>
            <text
              x={width / 2} 
              y={height / 2} 
              textAnchor="middle" 
              fontSize="14" 
              fill="black" 
            >
              Loading...
            </text>
          </svg>
        ); 
    }

    // Filter data for brush extent
    const filteredData = brushExtent 
        ? data.filter(d => brushExtent.includes(orderedDays[d.DayOrder])) 
        : data;

    // Prepare the color scale for HRV values
    const hrvValues = filteredData.map(d => d.HRV).filter(h => h != null); 
    const colorScale = scaleLinear()
        .domain([d3.min(hrvValues) || 0, d3.mean(hrvValues) || 0, d3.max(hrvValues) || 0])
        .range(['lightblue', 'blue', 'darkblue']); // Set your color range

    return (
        <svg width={width} height={height}>
            <text
                x={width / 2} 
                y={40} 
                textAnchor="middle" 
                className="title" 
            >
                THE HRV DISTRIBUTION
            </text>

            <image 
                href="dlr.png" // Adjust the path to your logo image
                x="800" // X position
                y="10" // Y position
                height="10px" // Adjust height as needed
                width="auto" // Maintain aspect ratio
            />

            <g transform={`translate(0, 10)`}>
                <Hexbinmap data={filteredData} worldAtlas={worldAtlas} />
            </g>
            <g transform={`translate(0, ${height - dateHistogramSize * height})`}>
            <DateHistogram
                    data={filteredData} // Use filtered data
                    width={width}
                    height={dateHistogramSize * height}
                    setBrushExtent={setBrushExtent}
                    xValue={d => orderedDays[d.DayOrder]} // Use DayOrder for x values
                />
            </g>

            {/* Position the color legend in the top right corner */}
            <g transform={`translate(700, 350)`}> {/* Adjust X and Y as needed */}
                <ColorLegend colorScale={colorScale} /> {/* Pass the color scale */}
            </g>
        </svg>
    );
};

export default App;
