import React from 'react';
import * as d3 from 'd3';

const ColorLegend = ({ colorScale }) => {
    const legendWidth = 300; // Width for the legend
    const legendHeight = 20; // Height for the gradient
    const numTicks = 5; // Number of ticks

    // Generate tick values based on the color scale's domain
    const ticks = d3.range(numTicks).map(i => 
        colorScale.invert(i / (numTicks - 1)) // Corresponding HRV values for ticks
    );

    return (
        <svg id="color-legend" width={legendWidth} height={legendHeight + 40}>
            <defs>
                <linearGradient id="gradient" x1="0%" y1="0%" x2="100%" y2="0%">
                    {ticks.map((tick, index) => (
                        <stop key={index} 
                              offset={`${(index / (numTicks - 1)) * 100}%`} 
                              stopColor={colorScale(tick)} />
                    ))}
                </linearGradient>
            </defs>
            <rect x="0" y="0" width={legendWidth} height={legendHeight} fill={`url(#gradient)`} className="rect-gradient" />

            {/* Add tick labels with a class for styling */}
            {ticks.map((tick, index) => (
                <text 
                    key={index} 
                    x={(index / (numTicks - 1)) * legendWidth} 
                    y={legendHeight + 15} 
                    fontSize="12" 
                    textAnchor="middle" 
                    className="legend-text"> {/* Added class for styling */}
                    {tick != null ? tick.toFixed(2) : ''}
                </text>
            ))}
            <text 
                x={legendWidth / 2} 
                y={legendHeight + 30} 
                textAnchor="middle" 
                fontWeight="bold">
                Average HRV
            </text>
        </svg>
    );
};

export default ColorLegend;
