import React from 'react';


const Instructions = () => {
    return (
        <div className="instructions">
            <h3>How to Operate</h3>
            <p>1. Slide the brush extension on the histogram to show the data. You can resize the brush by clicking and dragging on the edge, left or right, to block the data you want to display on the hexbin map.</p>
            <p>2. On the filters, you can choose your preferred data to display on the histogram and hexbin map. You can also choose the size of hexagonal shapes on the hexbin map.</p>
        </div>
    );
};

export default Instructions;
