import React, { useState } from 'react';
import * as d3 from 'd3'; 
import { hexbin as d3Hexbin } from 'd3-hexbin'; 

export const Marks = ({ bins, data, yValueField }) => {
    const [tooltip, setTooltip] = useState({ display: 'none' });

    if (!data || bins.length === 0) {
        return null; // Handle empty data or bins gracefully
    }

    const dynamicValues = data.map(d => d[yValueField]).filter(v => v != null);
    const colorScale = d3.scaleLinear()
        .domain([d3.min(dynamicValues), d3.mean(dynamicValues), d3.max(dynamicValues)]) 
        .range(['yellow', 'orange', 'red']); 

    return (
        <g className="marks">
            {bins.map((bin, i) => {
                const binData = data.filter(d => 
                    d.x >= bin.x - 15 && d.x <= bin.x + 15 &&
                    d.y >= bin.y - 15 && d.y <= bin.y + 15
                );

                const averageValue = binData.length > 0 
                    ? binData.reduce((sum, d) => sum + d[yValueField], 0) / binData.length 
                    : 0;

                const fillColor = colorScale(averageValue);

                return (
                    <g key={i}
                       onMouseEnter={(e) => {
                           setTooltip({
                               display: 'block',
                               x: e.clientX,
                               y: e.clientY,
                               content: `Count: ${bin.length}, Avg ${yValueField}: ${averageValue.toFixed(2)}`, 
                           });
                       }}
                       onMouseLeave={() => {
                           setTooltip({ display: 'none' });
                       }}>
                        <path 
                            d={d3Hexbin().hexagon(15)} 
                            transform={`translate(${bin.x}, ${bin.y})`} 
                            fill={fillColor} 
                            stroke="black" 
                            strokeWidth={1} 
                            opacity={1} 
                        />
                    </g>
                );
            })}
            <text 
                x={tooltip.x} 
                y={tooltip.y} 
                style={{ display: tooltip.display, pointerEvents: 'none' }} 
                fill="black" 
                fontSize="12" 
                textAnchor="middle">
                {tooltip.content}
            </text>
        </g>
    );
};
