import React from 'react';
import { scaleSqrt, max } from 'd3';
import { Marks } from './Marks';
import { Markscity } from './Markscity';
import { hexbin } from 'd3-hexbin';
import { geoNaturalEarth1 } from 'd3';

const sizeValue = d => d.HRV;

export const BubbleMap = ({ data, worldAtlas }) => {
    const projection = geoNaturalEarth1()
        .center([10.537004, 52.210297])
        .scale(160000)
        .translate([450, 300]);

    // Create the hexbin generator
    const hexbinGenerator = hexbin()
        .size([900, 750])
        .radius(20);

    // Generate hexagons from the projected data points, including HRV values for aggregation
    const bins = hexbinGenerator(
        data.map(d => ({
            ...d,
            coords: projection(d.coords),
        }))
    );

    return (
        <>
            <Marks bins={bins} sizeValue={sizeValue} />
            <Markscity worldAtlas={worldAtlas} />
        </>
    );
};
