import React, { useState } from 'react'; // Import useState for state management

const Instructions = ({ setCurrentPage }) => {
    const [isQGISChecked, setIsQGISChecked] = useState(false); // State for QGIS checkbox
    const [isD3Checked, setIsD3Checked] = useState(false); // State for D3 checkbox
    const [errorMessage, setErrorMessage] = useState(''); // State for error message

    const handleQGISRedirect = () => {
        // Check if QGIS checkbox is checked
        if (!isQGISChecked) {
            setErrorMessage('You must confirm that you have finished with the D3-React map before going to the questionnaire.'); // Show error message if not checked
        } else {
            // Redirect to the questionnaire URL
            window.open("https://forms.gle/8BJUm4goDgqYuvCw7", "_blank");
        }
    };

    const handleFinish = () => {
        // Check if D3 checkbox is checked
        if (!isD3Checked) {
            setErrorMessage('You must confirm that you have submitted the D3-React questionnaire before finishing this experiment.'); // Show error message if not checked
        } else {
            setErrorMessage(''); // Clear error message if checked
            setCurrentPage('final'); // Go to final page
        }
    };

    return (
        <div className="instructions">
            <h3>How to Operate</h3>
            <ul>
                <li>Again, if you see that these texts are covering the map, please ZOOM OUT (ctrl/command -) your browser.</li>
                <li>The displayed data on the hexbin map is based on the highlighted area that is marked on the histogram.</li>
                <li>Move the "brush" extension (blue square) on the histogram to display and change the data in the hexbin map. You can adjust the brush function by sliding it left or right, and resizing it by clicking and dragging on the edge, in or out.</li>
                <li>On the Filters, you can choose your preferred data to display on the histogram and hexbin map. Adjust your hexagonal size in “Hexbin size: filter.</li>
                <li>Point your mouse at a specific hexagonal shape on the map to find out its mean value and how many data points are contained within.</li>
                <li>You HAVE NO limited time to explore this map. ENJOY your wandering before you decided to Go to D3-React Questionnaire and then are allowed finish this experiment.
                </li>
            </ul>

            {/* Checkboxes and Buttons */}
            <div style={{ margin: '10px', marginTop: '50px' }}>
                {/* Checkbox for Go to D3-React Questionnaire */}
                <div>
                    <input
                        type="checkbox"
                        checked={isQGISChecked}
                        onChange={() => {
                            setIsQGISChecked(!isQGISChecked);
                            setErrorMessage(''); // Clear error message when checkbox changes
                        }}
                    />
                    <label style={{ marginLeft: '5px' }}>
                        I have finished with the map and ready to fill in the questionnaire.
                    </label>
                </div>

                <button
                    onClick={handleQGISRedirect} // Function to redirect to the questionnaire
                    className="d3-que"
                    style={{ marginLeft: '0px', padding: '10px 20px' }} // Button styling
                >
                    Go to D3-React Questionnaire
                </button>
                <br></br>
                <br></br>
                {/* Checkbox for Finish button */}
                <div>
                    <input
                        type="checkbox"
                        checked={isD3Checked}
                        onChange={() => {
                            setIsD3Checked(!isD3Checked);
                            setErrorMessage(''); // Clear error message on change
                        }}
                    />
                    <label style={{ marginLeft: '5px', marginTop: '60px' }}>
                        I have submitted the D3-React questionnaire in the Google Form.
                    </label>
                </div>

                <button
                    onClick={handleFinish} // Calls the updated handleFinish function
                    className="backd3"
                    style={{ marginLeft: '20px', padding: '10px 20px' }} // Button styling
                >
                    Finish
                </button>

                {/* Display Error Message */}
                {errorMessage && (
                    <div style={{ color: 'red', marginTop: '10px' }}>
                        {errorMessage}
                    </div>
                )}
            </div>
        </div>
    );
};

export default Instructions;
