import React, { useState } from 'react';
import * as d3 from 'd3'; // Import D3 for color scaling
import { hexbin as d3Hexbin } from 'd3-hexbin'; // Correctly import hexbin functionality

export const Marks = ({ bins, data, yValueField }) => {
    const [tooltip, setTooltip] = useState({ display: 'none' });

    const hrvValues = data.map(d => d[yValueField]).filter(h => h != null);
    const colorScale = d3.scaleLinear()
        .domain([d3.min(hrvValues), d3.mean(hrvValues), d3.max(hrvValues)]) 
        .range(['yellow', 'orange', 'red']); 

    return (
        <g className="marks">
            {bins.map((bin, i) => {
                const binData = data.filter(d => 
                    d.x >= bin.x - 15 && d.x <= bin.x + 15 &&
                    d.y >= bin.y - 15 && d.y <= bin.y + 15
                );

                const averageHRV = binData.length > 0 
                    ? binData.reduce((sum, d) => sum + d[yValueField], 0) / binData.length 
                    : 0;

                const fillColor = colorScale(averageHRV);

                return (
                    <g key={i}
                       onMouseEnter={(e) => {
                           setTooltip({
                               display: 'block',
                               x: e.clientX,
                               y: e.clientY,
                               content: `Count: ${bin.length}, Avg ${yValueField}: ${averageHRV.toFixed(2)}`, 
                           });
                       }}
                       onMouseLeave={() => {
                           setTooltip({ display: 'none' });
                       }}>
                        <path 
                            d={d3Hexbin().hexagon(5)} // Hexagon size can be adjusted here
                            transform={`translate(${bin.x}, ${bin.y})`} 
                            fill={fillColor} 
                            stroke="black" 
                            strokeWidth={1} 
                            opacity={1} 
                        />
                    </g>
                );
            })}
            <text 
                x={tooltip.x} 
                y={tooltip.y} 
                style={{ display: tooltip.display, pointerEvents: 'none' }} 
                fill="black" 
                fontSize="12" 
                textAnchor="middle">
                {tooltip.content}
            </text>
        </g>
    );
};
