import React, { useState } from 'react';

const Welcome = ({ onD3React, onQGIS, onFinish }) => {
    const [checkedState, setCheckedState] = useState(
        new Array(1).fill(false) // Initialize an array to track checkbox states
    );

    const [errorMessage, setErrorMessage] = useState('');

    const handleCheckboxChange = (position) => {
        const updatedCheckedState = checkedState.map((item, index) =>
            index === position ? !item : item
        );

        setCheckedState(updatedCheckedState);
        setErrorMessage(''); // Clear error message when checkbox changes
    };

    const handleFinish = () => {
        if (checkedState.some(item => item === false)) {
            setErrorMessage('You need to check all the questions to be eligible for this experiment.');
        } else {
            // If all checked, call the finish function
            onFinish();
        }
    };

    return (
        <div className="welcome-container">
            <h1 className="welcome-title">WELCOME!</h1>
            <p>You are going to evaluate the data visualization...</p>
            <p>Before starting, please answer the following questions:</p>

            <div className="checkboxes">
                <label>
                    <input
                        type="checkbox"
                        checked={checkedState[0]}
                        onChange={() => handleCheckboxChange(0)}
                    />
                    I am 18 years old or older
                </label>
            </div>

            {/* Display error message if validation fails */}
            {errorMessage && <p className="error-message">{errorMessage}</p>}

            <button onClick={onD3React} className="welcome-button">
                Go to D3.React App
            </button>
            <button onClick={onQGIS} className="welcome-button">
                Go to QGIS Map
            </button>
            <button onClick={handleFinish} className="welcome-button">
                Finish
            </button>
        </div>
    );
};

export default Welcome;
