import React, { useEffect } from 'react';
import * as d3 from 'd3';

const ColorLegend = ({ colorScale, position = { x: 20, y: 20 } }) => {
    useEffect(() => {
        const svg = d3.select('#color-legend')
            .attr("width", 200) // Reduce width of the legend
            .attr("height", 50); // Keep height constant but can adjust as needed

        const legendWidth = 180; // Adjust for smaller width
        const legendHeight = 15; // Smaller height for the gradient
        svg.selectAll("*").remove(); // Clear previous content

        // Add gradient
        const gradient = svg.append("defs")
            .append("linearGradient")
            .attr("id", "gradient")
            .attr("x1", "0%")
            .attr("y1", "0%")
            .attr("x2", "100%")
            .attr("y2", "0%");

        // Create gradient stops
        const ticks = colorScale.ticks(5);
        ticks.forEach((tick, index) => {
            gradient.append("stop")
                .attr("offset", `${(index / (ticks.length - 1)) * 100}%`)
                .attr("stop-color", colorScale(tick));
        });

        // Draw the gradient rectangle
        svg.append("rect")
            .attr("x", 0)
            .attr("y", 0)
            .attr("width", legendWidth)
            .attr("height", legendHeight)
            .style("fill", "url(#gradient)");

        // Add tick labels
        ticks.forEach((tick, index) => {
            svg.append("text")
                .attr("x", (index / (ticks.length - 1)) * legendWidth) 
                .attr("y", legendHeight + 15) 
                .attr("text-anchor", "middle")
                .style({
                    fontSize: "10px", // Smaller font size
                    fill: "black",
                })
                .text(tick.toFixed(2)); // Format HRV value
        });

        // Add title
        svg.append("text")
            .attr("x", legendWidth / 2)
            .attr("y", legendHeight + 30)
            .attr("text-anchor", "middle")
            .style({
                fontSize: "12px", // Smaller title font size
                fontWeight: "bold",
                fill: "black",
            })
            .text("Average HRV");

    }, [colorScale]);

    // Adjust position based on props
    return <svg id="color-legend" style={{ position: 'absolute', top: position.y, left: position.x }}></svg>;
};

export default ColorLegend;
