import { geoNaturalEarth1, geoPath, geoGraticule } from 'd3';

const projection = geoNaturalEarth1();
const path = geoPath(projection);
const graticule = geoGraticule();

export const Marks = ({
  worldAtlas: { land, interiors }, // Comes from useWorldAtlas
  data,
  sizeScale,
  sizeValue
}) => (
  <g className="marks">
    <path className="sphere" d={path({ type: 'Sphere' })} />
    <path className="graticules" d={path(graticule())} />
    {land.features.map(feature => (
      <path className="land" key={feature.id} d={path(feature)} />
    ))}
    <path className="interiors" d={path(interiors)} />
    {data.map(d => {
      const [x, y] = projection(d.coords);
      const radius = sizeScale(sizeValue(d));
      console.log("Circle position:", { x, y }, "With radius:", radius); // Debug log
      return (
        <circle
          key={d['Reported Date']}
          cx={x}
          cy={y}
          r={radius}
          style={{ fill: '#238010', opacity: 0.7 }} // Change color and opacity
        />
      );
    })}
  </g>
);
