import React, { useState } from 'react'; // Import useState for state management

const Instructions = ({ setCurrentPage }) => { // Accept setCurrentPage as a prop
    const [isChecked, setIsChecked] = useState(false); // State for checkbox
    const [errorMessage, setErrorMessage] = useState(''); // State for error message

    const handleFinish = () => {
        // Finish logic that checks if the checkbox is checked
        if (!isChecked) {
            setErrorMessage('You must confirm that you have finished the D3-React questionnaire before finishing this experiment.'); // Set error message if not checked
        } else {
            setErrorMessage(''); // Clear error message if checked
            setCurrentPage('final'); // Go to final page
        }
    };

    return (
        <div className="instructions">
            <h3>How to Operate</h3>
            <ul>
                <li>The data on hexbin map is based on the highlighted area that is marked on the histogram.</li>
                <li>Move the brush extension (blue square) on the histogram to display and change the data in hexbin map. You can slide it left or right, resize the brush by clicking and dragging on the edge, in or out.</li>
                <li>On the filters, you can choose your preferred data to display on the histogram and hexbin map. You can also choose the bin size of hexagonal shapes on the hexbin map.</li>
                <li>Point your mouse at a specific hexagonal shape on the map, to find out its mean value and how many data points are contained within.</li>
                <li>You do not have limited time to explore this map. Enjoy before you go to the D3-React questionnaire and finish this experiment.</li>
            </ul>

            {/* Confirmation Checkbox and Buttons */}
            <div style={{ margin: '20px', marginTop: '50px' }}>
                <input
                    type="checkbox"
                    checked={isChecked}
                    onChange={() => {
                        setIsChecked(!isChecked);
                        setErrorMessage(''); // Clear error message on change
                    }}
                />
                <label style={{ marginLeft: '0px' }}>
                    I have finished and submitted the D3-React questionnaire in the Google Form.
                </label>
                {/* Display Error Message */}
                {errorMessage && (
                    <div style={{ color: 'red', marginTop: '10px' }}>
                        {errorMessage}
                    </div>
                )}

                <div style={{ marginTop: '10px' }}>
                    <a href="https://forms.gle/8BJUm4goDgqYuvCw7" target="_blank" rel="noopener noreferrer">
                        <button className="d3-que" style={{ marginLeft: '0px', padding: '10px 20px' }}>
                            Go to D3-React Questionnaire
                        </button>
                    </a>
                    <button
                        onClick={handleFinish} // Calls the updated handleFinish function
                        className="backd3"
                        style={{ marginLeft: '20px', padding: '10px 20px' }} // Same styling as above
                    >
                        Finish
                    </button>
                </div>
            </div>
        </div>
    );
};

export default Instructions;
