import React, { useState } from 'react';

const Declaration = ({ onStart }) => {
    const [checkedState, setCheckedState] = useState(new Array(5).fill(false)); // 4 checkboxes
    const [errorMessage, setErrorMessage] = useState('');

    const handleCheckboxChange = (position) => {
        const updatedCheckedState = checkedState.map((item, index) =>
            index === position ? !item : item
        );

        setCheckedState(updatedCheckedState);
        setErrorMessage(''); // Clear error message when checkbox changes
    };

    const handleStart = () => {
        if (checkedState.some(item => !item)) {
            setErrorMessage('You must check all declarations to eligible for this experiment.');
        } else {
            setErrorMessage(''); // Clear previous messages
            onStart(); // Proceed to the Welcome page
        }
    };

    return (
        <div className="declaration-container">
            <h1>Welcome to the Data Visualization Experiment</h1>
            <h3>You are going to evaluate the "hexagonal bin map (hexbin)" data visualization between the static map and the interactive map.</h3>
            <p className="checkboxes">
                As a participant, I declare that ...
            </p>
            <div className="checkboxes">
                <label>
                    <input
                        type="checkbox"
                        checked={checkedState[0]}
                        onChange={() => handleCheckboxChange(0)}
                    />
                    I am 18 years old and older
                </label>
                <label>
                    <input
                        type="checkbox"
                        checked={checkedState[1]}
                        onChange={() => handleCheckboxChange(1)}
                    />
                    I have no colour blindness
                </label>
                <label>
                    <input
                        type="checkbox"
                        checked={checkedState[2]}
                        onChange={() => handleCheckboxChange(2)}
                    />
                    I am not suffering from Trypophobia or fear of hexagonal shapes
                </label>
                <label>
                    <input
                        type="checkbox"
                        checked={checkedState[3]}
                        onChange={() => handleCheckboxChange(3)}
                    />
                    I agree to do this experiment
                </label>
                <label>
                    <input
                        type="checkbox"
                        checked={checkedState[4]}
                        onChange={() => handleCheckboxChange(4)}
                    />
                    I am allowed to stop this experiment for specific reason
                </label>
            </div>

            {/* Display error message if validation fails */}
            {errorMessage && <p className="error-message">{errorMessage}</p>}

            <button onClick={handleStart} className="welcome-button">
                START
            </button>
        </div>
    );
};

export default Declaration;
