import React, { useEffect } from 'react';
import * as d3 from 'd3';

const ColorLegend = ({ colorScale }) => {
    useEffect(() => {
        const svg = d3.select('#color-legend')
            .attr("width", 300)
            .attr("height", 70);

        const legendWidth = 150; // Width for the gradient
        const legendHeight = 10; // Height for the gradient
        svg.selectAll("*").remove(); // Clear previous content

        // Add gradient
        const gradient = svg.append("defs")
            .append("linearGradient")
            .attr("id", "gradient")
            .attr("x1", "0%")
            .attr("y1", "0%")
            .attr("x2", "100%")
            .attr("y2", "0%");

        // Create gradient stops
        const ticks = colorScale.ticks(3);
        ticks.forEach((tick, index) => {
            gradient.append("stop")
                .attr("offset", `${(index / (ticks.length - 1)) * 100}%`)
                .attr("stop-color", colorScale(tick));
        });

        // Draw gradient rectangle
        svg.append("rect")
            .attr("class", "gradient-rect") // Class for gradient rectangle
            .attr("x", 0)
            .attr("y", 20)
            .attr("width", legendWidth)
            .attr("height", legendHeight)
            .style("fill", "url(#gradient)");

        // Add tick labels
        ticks.forEach((tick, index) => {
            svg.append("text")
                .attr("class", "legend-tick") // Class for tick labels
                .attr("x", (index / (ticks.length - 1)) * legendWidth + 12) 
                .attr("y", legendHeight + 30) 
                .attr("text-anchor", "middle")
                .text(tick.toFixed(2)); // Format HRV value
        });

    }, [colorScale]);

    return <svg id="color-legend"></svg>; // Placeholder for the legend
};

export default ColorLegend;
