import React, { useState } from 'react';
import * as d3 from 'd3'; // Import d3 for scales
import { hexbin as d3Hexbin } from 'd3-hexbin'; // Ensure hexbin is imported correctly

export const Marks = ({ bins, data }) => {
    const [tooltip, setTooltip] = useState({ display: 'none' });

    // Prepare the color scale for HRV values. Remove null values to avoid errors.
    const hrvValues = data.map(d => d.HRV).filter(h => h != null);
    const colorScale = d3.scaleLinear()
        .domain([d3.min(hrvValues), d3.mean(hrvValues), d3.max(hrvValues)]) // Set domain
        .range(['blue', 'lightblue', 'darkblue']); // Define color range

    return (
        <g className="marks">
            {bins.map((bin, i) => {
                // Filter data points that belong to this bin using the coordinates.
                const binData = data.filter(d => 
                    d.x >= bin.x - 15 && d.x <= bin.x + 15 && // Check x range
                    d.y >= bin.y - 15 && d.y <= bin.y + 15    // Check y range
                );

                // Calculate average HRV for this bin
                const averageHRV = binData.length > 0 
                    ? binData.reduce((sum, d) => sum + d.HRV, 0) / binData.length 
                    : 0;

                // Set the fill color based on average HRV
                const fillColor = colorScale(averageHRV); // Use average HRV to determine color

                return (
                    <g key={i} 
                       onMouseEnter={(e) => {
                           setTooltip({
                               display: 'block',
                               x: e.clientX,
                               y: e.clientY,
                               content: `Count: ${bin.length}, Avg HRV: ${averageHRV.toFixed(2)}`, // Show tooltip
                           });
                       }}
                       onMouseLeave={() => {
                           setTooltip({ display: 'none' });
                       }}>
                    <path
                        d={d3Hexbin().hexagon(5)} // Define hexagon size
                        transform={`translate(${bin.x}, ${bin.y})`} // Positioning the hexagon
                        fill={fillColor} // Use calculated color based on HRV
                        stroke="black" // Stroke color for borders
                        strokeWidth={1} // Width of the stroke
                        opacity={0.7} // Adjust opacity for visibility
                    />
                    </g>
                );
            })}
            {/* Tooltip for hover information */}
            <text 
                x={tooltip.x} 
                y={tooltip.y} 
                style={{ display: tooltip.display, pointerEvents: 'none' }} 
                fill="black" 
                fontSize="12" 
                textAnchor="middle">
                {tooltip.content}
            </text>
        </g>
    );
};
