import { geoNaturalEarth1, geoPath, geoGraticule, index } from 'd3';

const projection = geoNaturalEarth1()
  .center([10.527004, 52.210297]) // Center over Braunschweig, Germany
  .scale(150000) // Adjust scale for zoom level; increase for more detail
  .translate([450, 300]); // Adjust based on your SVG dimensions; // Original projection
const path = geoPath(projection);
const graticule = geoGraticule();

const locations = [
  { name: "Braunschweig Rathaus", coords: [10.5266, 52.2634] },
  { name: "Braunschweig Hbf", coords: [10.5260, 52.2672] },
  { name: "Wolfenb端ttel Hbf", coords: [10.5261, 52.1644] },
  { name: "Wolfenb端ttel Schloss", coords: [10.5265, 52.1629] },
];

export const Markscity = ({
  worldAtlas: { land, interiors },
  data,
  sizeScale,
  sizeValue
}) => (
  <g className="markscity">
  {locations.map((location, index) => {
    const [lon,lat] = location.coords;
    const [x, y] = projection(location.coords);
    return (
      <g key={index}> {/* Group for Braunschweig circle and text */}
        <circle 
          cx={x} 
          cy={y} 
          r={5}
        />
        <text
          className="location-label"
          textAnchor="left"
          x={x} // Align text with the circle
          y={y - 10} // Position text above the circle
        >
          {location.name} 
        </text>
      </g>
    );
  })}
</g>

);
