import React from 'react';
import { hexbin } from 'd3-hexbin';
import { geoNaturalEarth1 } from 'd3';
import { Marks } from './Marks';
import { Markscity } from './Markscity';
import ColorLegend from './ColorLegend'; // Import the legend

export const BubbleMap = ({ data, worldAtlas }) => {
    const Width = 900;
    const Height = 750;

    const projection = geoNaturalEarth1()
        .center([10.537004, 52.210297])
        .scale(160000)
        .translate([Width / 2, Height / 2]);

    const hexbinGenerator = hexbin()
        .extent([[0, 0], [Width,Height]])
        .radius(7); // Adjust the radius based on desired hex size

    // Project data with HRV
    const projectedData = data.map(d => {
        const coords = projection(d.coords); // Ensure d.coords contains [longitude, latitude]
        return { ...d, x: coords[0], y: coords[1], HRV: d.HRV }; // Include HRV here
    });

