        .range(['blue', 'lightblue', 'darkblue']); // Set your color range


    return (
        <svg width={Width} height={Height}>
            <Marks bins={bins} data={projectedData} /> {/* Pass bins and the original data */}
            <Markscity
              worldAtlas={worldAtlas} />
            <ColorLegend colorScale={colorScale} /> {/* Include the color legend */}
        </svg>
    );
};
