import React, { useEffect } from 'react';
import * as d3 from 'd3';

const ColorLegend = ({ colorScale }) => {
    useEffect(() => {
        const svg = d3.select('#color-legend') // Select the legend SVG
            .attr("width", 300)
            .attr("height", 50);

        const legendWidth = 260; // Width for the gradient
        const legendHeight = 20; // Height for the gradient

        // Clear any previous content
        svg.selectAll("*").remove();

        // Add gradient
        const gradient = svg.append("defs")
            .append("linearGradient")
            .attr("id", "gradient")
            .attr("x1", "0%")
            .attr("y1", "0%")
            .attr("x2", "100%")
            .attr("y2", "0%");

        // Create the gradient stops based on the color scale
        const stops = colorScale.ticks(5).map((tick, index) => {
            return {
                offset: `${(index / 4) * 100}%`, // Calculate the position of the stop
                color: colorScale(tick) // Get the color for the stop
            };
        });

        stops.forEach(stop =>
            gradient.append("stop")
                .attr("offset", stop.offset)
                .attr("stop-color", stop.color)
        );

        // Draw the gradient rectangle
        svg.append("rect")
            .attr("x", 0)
            .attr("y", 0)
            .attr("width", legendWidth)
            .attr("height", legendHeight)
            .style("fill", "url(#gradient)");

        // Add tick labels
        const ticks = colorScale.ticks(5); // Use the same ticks as the gradient
        ticks.forEach((tick, index) => {
            svg.append("text")
                .attr("x", (index / (ticks.length - 1)) * legendWidth) // Tick position
                .attr("y", legendHeight + 15) // Position below the gradient
                .attr("text-anchor", "middle")
                .text(tick.toFixed(2)); // Format the HRV value to 2 decimal places
        });

        // Add title
        svg.append("text")
            .attr("x", legendWidth / 2)
            .attr("y", legendHeight + 30)
            .attr("text-anchor", "middle")
            .text("Average HRV")
            .attr("font-weight", "bold");

    }, [colorScale]); // Redraw when colorScale changes

    return (
        <svg id="color-legend"></svg> // Placeholder for the legend
    );
};

export default ColorLegend;
