import React from 'react';
import * as d3 from 'd3';

const ColorLegend = ({ colorScale }) => {
    const legendWidth = 300; // Set the legend width
    const legendHeight = 20; // Set the legend height
    const numTicks = 5;

    // Generate tick values based on the color scale's domain
    const ticks = d3.range(numTicks).map(i => 
        colorScale.invert(i / (numTicks - 1)) // Map to actual HRV values

    );
    // Generate the gradient color for the legend
    return (
        <svg width={legendWidth} height={legendHeight}>
            <defs>
                <linearGradient id="gradient" x1="0%" y1="0%" x2="100%" y2="0%">
                    {colorScale.ticks().map((tick) => (
                        <stop 
                            key={index} 
                            offset={`${(index / (numTicks - 1)) * 100}%`} 
                            stopColor={colorScale(tick)} />
                    ))}
                </linearGradient>
            </defs>
            <rect x="0" y="0" width={legendWidth} height={legendHeight} fill="url(#gradient)" /> 
            {ticks.map((tick, index) => (
                <text 
                    key={index} 
                    x={(index / (numTicks - 1)) * legendWidth} 
                    y={legendHeight + 15} 
                    fontSize="12" 
                    textAnchor="middle">
                    {tick != null ? tick.toFixed(2) : ''} 
                </text>
            ))}
            <text 
                x={legendWidth / 2} 
                y={legendHeight + 15} 
                textAnchor="middle" 
                fontWeight="bold">
                Average HRV
            </text>
        </svg>
    );
};

export default ColorLegend;
