import React from 'react';
import { Marks } from './Marks';
import { Markscity } from './Markscity';
import { hexbin } from 'd3-hexbin';
import { geoNaturalEarth1 } from 'd3';

export const BubbleMap = ({ data, worldAtlas }) => {
    // Adjusted projection values
    const projection = geoNaturalEarth1()
        .center([0, 0]) // Set center to (0, 0) for testing
        .scale(100) // Adjust scale to see if it helps with visibility
        .translate([450, 300]); // Center your SVG

    const hexbinGenerator = hexbin()
        .size([900, 750])
        .radius(30); // Set radius for visibility

    // Test with hardcoded points
    const testPoints = [
        { coords: [10.537004, 52.210297], HRV: 50 }, // Close to original center
        { coords: [10.54, 52.21], HRV: 80 },
        { coords: [10.53, 52.20], HRV: 100 },
    ];

    // Projecting test coordinates
    const projectedTestData = testPoints.map(d => {
        const coords = projection(d.coords);
        console.log("Projected Coords (Test):", coords, "HRV:", d.HRV);
        return { ...d, coords };
    });

    // Generate hex bins from projected test data
    const testBins = hexbinGenerator(projectedTestData);
    console.log("Test Hex Bin Data:", testBins); // Check test bins

    return (
        <>
            <Marks bins={testBins} />
            <Markscity worldAtlas={worldAtlas} />
        </>
    );
};
