// useData.js
import { useEffect, useState } from 'react';
import { csv } from 'd3';

const csvUrl = '/dataku.csv'; // Path to the CSV file in public

const row = (d) => {
    d.coords = d['Location Coordinates']
        .split(',')
        .map((coord) => +coord.trim())
        .reverse(); // Convert coordinates to numbers
    d['Total Dead and Missing'] = +d['Total Dead and Missing']; // Convert to number
    d['Reported Date'] = new Date(d['Reported Date']); // Ensure it's a Date
    return d;
};

export const useData = () => {
    const [data, setData] = useState(null);

    useEffect(() => {
        csv(csvUrl, row) // Load the CSV with processing function
            .then(setData)
            .catch(error => console.error("Error loading data:", error));
    }, []);

    return data; // Return the processed CSV data
};
