import React, { useState } from 'react';
import { hexbin as d3Hexbin } from 'd3-hexbin';
import * as d3 from 'd3';

export const Marks = ({ bins, data }) => {
    const [tooltip, setTooltip] = useState({ display: 'none' });

    // Define the color scale based on HRV values
    const hrvValues = data.map(d => d.HRV); // Collect all HRV values for scaling purpose
    const colorScale = d3.scaleLinear()
        .domain([d3.min(hrvValues), d3.mean(hrvValues), d3.max(hrvValues)])
        .range(['blue', 'lightblue', 'darkblue']); // Adjust the range as needed

    return (
        <g className="marks">
            {bins.map((bin, i) => {
                // Get the points in this bin
                const binData = data.filter(d => 
                    // Check if the data point belongs in the current hexagon bin
                    hexbinGenerator([d.x, d.y]).includes(bin)
                );

                // Calculate average HRV for this bin
                const averageHRV = binData.length > 0 
                    ? binData.reduce((sum, d) => sum + d.HRV, 0) / binData.length 
                    : 0;

                // Use the color scale to determine the fill color based on average HRV
                const fillColor = colorScale(averageHRV);

                return (
                    <g key={i} 
                       onMouseEnter={(e) => {
                           setTooltip({
                               display: 'block',
                               x: e.clientX,
                               y: e.clientY,
                               content: `Count: ${bin.length}, Avg HRV: ${averageHRV.toFixed(2)}`,
                           });
                       }}
                       onMouseLeave={() => {
                           setTooltip({ display: 'none' });
                       }}>
                    <path
                        d={d3Hexbin().hexagon(15)} // Hexagon shape
                        transform={`translate(${bin.x}, ${bin.y})`} // Position using centroid
                        fill={fillColor} // Use the calculated color
                        stroke="black" // Stroke color
                        strokeWidth={1} // Stroke width
                        opacity={0.7} // Adjust opacity if desired
                    />
                    </g>
                );
            })}
            {/* Tooltip */}
            <text 
                x={tooltip.x} 
                y={tooltip.y} 
                style={{ display: tooltip.display, pointerEvents: 'none' }} 
                fill="black" 
                fontSize="12" 
                textAnchor="middle">
                {tooltip.content}
            </text>
        </g>
    );
};
