import React, { useState } from 'react';

const QGIS = ({ onBack }) => {
    const [isQGISChecked, setIsQGISChecked] = useState(false); // State to track the QGIS checkbox
    const [isD3Checked, setIsD3Checked] = useState(false); // State to track the D3 checkbox
    const [errorQGISMessage, setErrorQGISMessage] = useState(''); // State for error message for QGIS
    const [errorD3Message, setErrorD3Message] = useState(''); // State for error message for D3

    const handleQGISCheckboxChange = () => {
        setIsQGISChecked(!isQGISChecked); // Toggle QGIS checkbox state
        setErrorQGISMessage(''); // Clear error message when checkbox changes
    };

    const handleD3CheckboxChange = () => {
        setIsD3Checked(!isD3Checked); // Toggle D3 checkbox state
        setErrorD3Message(''); // Clear error message when checkbox changes
    };

    const handleQGISRedirect = () => {
        if (!isQGISChecked) {
            setErrorQGISMessage('Please confirm that you have finished with the map and are ready to fill in the questionnaire before proceeding.'); // Set error message for QGIS
        } else {
            window.open("https://forms.gle/hNiaZBJKq7en76wB7", "_blank"); // Redirect to the questionnaire
        }
    };

    const handleD3Redirect = () => {
        if (!isD3Checked) {
            setErrorD3Message('Please confirm that you have submitted the QGIS questionnaire before proceeding to the D3-React App.'); // Set error message for D3
        } else {
            onBack(); // Go back to D3 React App
        }
    };

    return (
        <div style={{ display: 'flex', flexDirection: 'column', height: '100vh', justifyContent: 'space-between', padding: '20px' }}>
            <div style={{ textAlign: 'center' }}>
                <h1>QGIS Map</h1>
                <p>Here is the data visualization, enjoy your time before deciding to Go to QGIS Questionnaire!</p>
                {/* Display your QGIS images here */}
                <img src="hrv.png" alt="QGIS Example 1" style={{ width: '450px', margin: '5px' }} />
                <img src="stress.png" alt="QGIS Example 2" style={{ width: '450px', margin: '5px' }} />
                <img src="hrv.png" alt="QGIS Example 3" style={{ width: '450px', margin: '5px' }} />
                <img src="stress.png" alt="QGIS Example 4" style={{ width: '450px', margin: '5px' }} />
                {/* Add more images as needed */}
            </div>

            <div style={{ display: 'flex', justifyContent: 'space-around', marginTop: '5px', marginBottom: '100px' }}> {/* Spacing adjustments */}
                <div style={{ textAlign: 'center' }}>
                    {/* Checkbox for Go to QGIS Questionnaire */}
                    <div style={{ marginBottom: '5px' }}>
                        <input
                            type="checkbox"
                            checked={isQGISChecked}
                            onChange={handleQGISCheckboxChange}
                        />
                        <label style={{ marginLeft: '5px' }}>
                            I have finished with the map and I'm ready to fill in the questionnaire
                        </label>
                        {errorQGISMessage && <p style={{ color: 'red' }}>{errorQGISMessage}</p>} {/* Display error message for QGIS */}
                    </div>

                    <button
                        onClick={handleQGISRedirect} // Function to redirect to the questionnaire
                        className="qgis-que"
                        style={{ margin: '5px', padding: '10px 20px' }} // Button styling
                    >
                        Go to QGIS Questionnaire
                    </button>
                </div>

                <div style={{ textAlign: 'center' }}>
                    {/* Checkbox for Go to D3-React App */}
                    <div style={{ marginBottom: '5px' }}>
                        <input
                            type="checkbox"
                            checked={isD3Checked}
                            onChange={handleD3CheckboxChange}
                        />
                        <label style={{ marginLeft: '5px' }}>
                            I have submitted the QGIS questionnaire
                        </label>
                        {errorD3Message && <p style={{ color: 'red' }}>{errorD3Message}</p>} {/* Display error message for D3 */}
                    </div>

                    <button
                        onClick={handleD3Redirect} // Function for redirecting to D3 React App
                        className="qgis-back"
                        style={{ margin: '5px', padding: '10px 20px' }} // Button styling
                    >
                        Go to D3-React App
                    </button>
                </div>
            </div>
        </div>
    );
};

export default QGIS;
